                            $rating = 5;
                        }
                        $rating = max(0, min(5, $rating));

                        $fullStars = floor($rating);
                        $decimal = $rating - $fullStars;
                        $hasHalf = $decimal >= 0.25 && $decimal < 0.75;
                        $extraFull = $decimal >= 0.75;

                        if ($extraFull) {
                            $fullStars++;
                            $hasHalf = false;
                        }

                        $printed = $fullStars + ($hasHalf ? 1 : 0);
                    @endphp

